// DeleteStudent.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const DeleteStudent = () => {
  const [rollNo, setRollNo] = useState('');
  const [message, setMessage] = useState('');

  const handleDeleteStudent = async () => {
    try {
      const res = await axios.delete(`http://localhost:5000/api/students/${rollNo}`);
      setMessage(res.data.message || 'Student deleted successfully');
      setRollNo('');
    } catch (err) {
      setMessage('Error deleting student. Please make sure the roll number is correct.');
      console.error('Error deleting student:', err);
    }
  };

  return (
    <div className="delete-student">
      <h1>Delete Student</h1>
      <div className="form">
        <input
          type="text"
          placeholder="Roll No"
          value={rollNo}
          onChange={(e) => setRollNo(e.target.value)}
          style={{ width: '150px', padding: '10px' }}
        />
        <button onClick={handleDeleteStudent}>Delete Student</button>
      </div>
      {message && <p>{message}</p>}
      <center><Link to='/Student'>Go back to Student Management</Link></center>
    </div>
  );
}

export default DeleteStudent;
